#include <stdio.h>
#include <string.h>

int main() {
    char buffer[100];
    char *input = "Hello, World!";
    strncpy(buffer, input, sizeof(buffer) - 1);
    buffer[sizeof(buffer) - 1] = '\0';  // Explicit null termination
    printf("%s", buffer);
    return 0;
}

